// Trove 없으면 Trove 만들어. 에러메세지
// ICR이 MCR 이상인지? 이상이 아니라면 에러메세지
State.init({ option: "withdraw", token: "ETH" });

const optionHandler = (option) => {
  State.update({ option: option });
};
const tokenHandler = (token) => {
  State.update({ token: token });
};

return (
  <ManageWrapper>
    <div className="option-wrapper">
      <button
        className={`option ${
          state.option === "deposit" ? "active" : "disabled"
        }`}
        onClick={() => {
          optionHandler("deposit");
        }}
      >
        deposit
      </button>
      <button
        className={`option ${
          state.option === "withdraw" ? "active" : "disabled"
        }`}
        onClick={() => {
          optionHandler("withdraw");
        }}
      >
        withdraw
      </button>
    </div>
    <div className="input-wrapper">
      <input
        type="number"
        placeholder={state.token === "ETH" ? "0.0000 ETH" : "0.0000 LUSD"}
      ></input>
      <div className={`token-wrapper`}>
        <button
          onClick={() => {
            tokenHandler("ETH");
          }}
          className={`token ${state.token === "ETH" ? "active" : "disabled"}`}
        >
          ETH
        </button>
        <button
          onClick={() => {
            tokenHandler("LUSD");
          }}
          className={`token ${state.token === "LUSD" ? "active" : "disabled"}`}
        >
          LUSD
        </button>
      </div>
    </div>
    <div className="confirm-wrapper">
      <button className={`confirm`}></button>
    </div>
  </ManageWrapper>
);
