const DashboardWrapper = styled.div`

`;

const accountId = context.accountId;

console.log("accountId", accountId);
if (!accountId) {
  return "Please sign in with NEAR wallet";
}

const items = Social.get(`${accountId}/`);

console.log("items", items);
const myState = State.init({
  key: "",
  value: "",
  allItems: {},
});

console.log("keyState", myState);

function addItem() {
  let currItems = myState.allItems;
  currItems[myState.key] = myState.value;
  State.update({
    key: "",
    value: "",
    allItems: currItems,
  });
  console.log("My state", myState);
}
return (
  <DashboardWrapper>
    <h1>Add new item: </h1>
    <label for="key">Key: </label>
    <input
      type="text"
      onChange={(e) => State.update({ key: e.target.value })}
      value={myState.key}
      id="key"
    />
    <label for="value">Value: </label>
    <input
      type="text"
      id="value"
      value={myState.value}
      onChange={(e) => State.update({ value: e.target.value })}
    />
    <button onClick={addItem}>Add item</button>
    <CommitButton data={{ testWidget: myState.allItems }}>
      Upload data
    </CommitButton>
  </DashboardWrapper>
);
