const ownerId = "contribut3.near";
const accountId = props.accountId;
const notStandalone = props.notStandalone ?? false;
const isPreview = props.isPreview ?? false;

if (!accountId) {
  return "Cannot show contributor without account ID!";
}

const contributor = isPreview
  ? props.contributor
  : Near.view(
    ownerId,
    "get_contributor",
    { account_id: accountId },
    "final",
    true
  );

if (!contributor) {
  return isPreview
    ? "You must provide a contributor object in preview mode"
    : "Loading...";
}

const entity = Near.view(
  ownerId,
  "get_entity",
  { account_id: accountId },
  "final"
);

const isEntity = !!entity;
const active = contributor.looking_for_work;

const profile = Social.getr(`${accountId}/profile`);

const contributionTypes = contributor.contribution_types.reduce(
  (ob, contributionType) =>
    typeof contributionType === "object"
      ? { ...ob, [contributionType.Other]: "" }
      : { ...ob, [contributionType]: "" },
  {}
);

if ("Other" in contributionTypes) {
  contributionTypes[contributionTypes.Other] = "";
  delete contributionTypes.Other;
}

const skills = contributor.skills.reduce(
  (ob, skill) => ({ ...ob, [skill]: "" }),
  {}
);

const tags = { ...skills, ...contributionTypes } || profile.tags;

const body = (
  <div
    className="d-flex flex-row justify-content-start"
    id={accountId}
    style={{ minHeight: "8em" }}
  >
    <div className="flex-grow-1 py-3">
      <Widget
        src={`${ownerId}/widget/ProfileLine`}
        props={{
          accountId,
          isEntity,
          imageSize: "3em",
          additionalColumn: inboxView ? (
            <></>
          ) : (
            <div className="d-flex flex-row justify-content-between align-items-center">
              <div
                className={`text-${active ? "success" : "muted"
                  } me-3 d-flex flex-row justify-content-end align-items-center fs-6`}
                style={{ minWidth: "7em" }}
              >
                {active ? (
                  <i
                    className="d-block bg-success rounded-circle"
                    style={{ width: ".6em", height: ".6em" }}
                  />
                ) : (
                  <></>
                )}
                <span className="ms-1">
                  {active ? "Available" : "Not available"}
                </span>
              </div>
              <Widget
                src={`${ownerId}/widget/CardMenu`}
                props={{
                  items: [
                    {
                      text: "Propose contribution",
                      icon: "bi-person-up",
                      id: "contribute",
                    },
                    {
                      text: "Invite to contribute",
                      icon: "bi-person-plus",
                      id: "invite",
                    },
                    {
                      text: "View details",
                      icon: "bi-info-circle",
                      id: "info",
                    },
                    {
                      text: "Share",
                      icon: "bi-arrow-up-right",
                      id: "share",
                    },
                  ],
                }}
              />
            </div>
          ),
          additionalRow: (
            <>
              <div className="d-flex flex-row justify-content-between align-items-center">
                <i
                  className={`d-block ${isEntity ? "bi-diagram-2" : "bi-person"
                    }`}
                />
                <span className="ms-2">
                  {isEntity ? "Organization" : "Individual contributor"}
                </span>
              </div>
              <Widget src={`${ownerId}/widget/Tags`} props={{ tags }} />
              <Widget
                src={`${ownerId}/widget/DescriptionArea`}
                props={{
                  description:
                    contributor.resume ||
                    entity?.description ||
                    profile.description,
                }}
              />
            </>
          ),
        }}
      />
    </div>
  </div>
);

return (
  <div className="border-bottom border-secondary-subtle">
    <div className="px-3 py-0">{body}</div>
  </div>
);
