const getTokenBalance = () => {
  const erc20 = new ethers.Contract(
    voterTokenContract,
    tokenAbi,
    Ethers.provider().getSigner()
  );

  erc20.balanceOf(state.sender).then((rawBalance) => {
    State.update({
      balance: rawBalance.toString(),
    });
  });
};

const getVote = (_signer) => {
  const erc20 = new ethers.Contract(
    voterTokenContract,
    tokenAbi,
    Ethers.provider().getSigner()
  );

  erc20.hasMinted(state.sender).then((_hasMinted) => {
    if (_hasMinted) {
      State.update({ hasMinted: true });
      getTokenBalance();
    }
  });

  erc20.mint().then((transactionHash) => {
    State.update({ hashMinted: true });
    console.log("transactionHash is " + transactionHash);
  });
};

