// Styles
const Field = styled.div`
  margin-top: 10px;
`;

// States
State.init({
  form: {
    name: "",
    avatar: "",
    website: "",
  },
});

/*
 * Handle input change
 */
const onInputChange = ({ target }) => {
  //console.log("target", target);
  const fieldName = target.dataset.name;
  let value = target.value;
  const fieldType = target.dataset.type;
  if (fieldType === "int") {
    value = parseInt(value);
  }

  let form = state.form;
  form[fieldName] = value;

  State.update({ form });
};

/*
 * Submit form
 */
const handleSubmit = async (e) => {
  e.preventDefault(); // Avoids refreshing the page
  console.log("Submit!!!");
};

return (
  <>
    <div class="container border border-info p-3">
      <div class="container">
        <Field>
          <label>Name</label>
          <input
            type="text"
            class="form-control"
            placeholder="Enter your name"
            onChange={onInputChange}
            data-name="name"
            data-type="string"
            value={state.form[`name`] ? state.form[`name`] : ``}
          />
        </Field>
        <Field>
          <label>Avatar</label>
          <input
            type="text"
            class="form-control"
            placeholder=""
            onChange={onInputChange}
            data-name="avatar"
            data-type="string"
            value={state.form[`avatar`] ? state.form[`avatar`] : ``}
          />
        </Field>
        <Field>
          <label>Website</label>
          <input
            type="text"
            class="form-control"
            placeholder=""
            onChange={onInputChange}
            data-name="website"
            data-type="string"
            value={state.form[`website`] ? state.form[`website`] : ``}
          />
        </Field>
      </div>

      <div class="container p-3 text-center">
        <button
          onClick={() => {
            console.log("onClick");
          }}
