const setDept = (depositChangeEvent) => {
  const dept = Number(depositChangeEvent.target.value);
  const { totalDept } = state;
  const collateralRatio = ((dept * 1882) / Number(totalDept)) * 100;

  State.update({ dept, collateralRatio });

  validateTrove();
};

const setBorrow = (borrowChangeEvent) => {
  const { dept, liquidationReserve } = state;
  const borrow = Number(borrowChangeEvent.target.value);
  const borrowingFee = (borrow * 0.5) / 100;
  const totalDept =
    borrow + Number(borrowingFee.toFixed(2)) + liquidationReserve;
  const collateralRatio = ((dept * 1882) / Number(totalDept)) * 100;

  State.update({ borrow, borrowingFee, totalDept, collateralRatio });
  validateTrove();
};

const validateTrove = () => {
  const { dept, borrow, totalDept } = state;

  console.log({ dept, borrow });
  if (borrow < 1800) {
    State.update({ msg: "Borrow must be at least 1800 LUSD." });
    return;
  }

  const collateralRatio = ((dept * 1882) / Number(totalDept)) * 100;
  if (collateralRatio < 1.1) {
    State.update({ msg: "Collateral ratio must be at least 110%." });
    return;
  }

  State.update({ msg: "" });
};

