const Container = styled.div`
   background: #1A2E33;
    .table{
        margin:0;
        border-bottom:2px solid rgba(48, 67, 82, 0.5);
    }
    .table thead tr{
        height:50px;
        border:hidden;
    }
    .table tbody tr{
        height:50px;
    }
    .table.click tbody tr{
      cursor:pointer;
    }
     .table.click tbody tr:hover{
        background: rgba(0, 0, 0, 0.1);
     }
    .table th{
        color: #7E8A93;
        font-size:14px;
        vertical-align: middle;
    }
    .table td{
        color: #fff;
        font-size:14px;
        vertical-align: middle;
        border: none;
    }
    .tokenIcon{
      width: 26px;
      height: 26px;
      border-radius:100px;
      margin-right:8px;
    }
    .rewardIcon{
      width: 16px;
      height: 16px;
      border-radius:100px;
    }
    .text_red_color{
      color:#FF6BA9;
    }
    .ml_4_ne{
        margin-left:-4px;
    }
    .flex_center{
      display:flex;
      align-items:center;
    }
    .modal-content{
      background-color:#1A2E33;
      border-radius:12px;
    }
    .modal-header{
      display:flex;
      align-items:center;
      justify-content:center;
      color:#fff;
      font-weight: 700;
      font-size: 18px;
      border-bottom:2px solid rgba(48, 67, 82, 0.5)
    } 
    .modal-header .btn-close{
      position:absolute;
      right:28px;
      margin:0;
    }
    .modal-footer{
      border:none;
    }
    .modal-body .tab{
      display:flex;
      align-items:center;
      justify-content:space-between;
      margin-bottom:30px;
    }
    .modal-body .tab span{
      display:flex;
      align-items:center;
      justify-content:center;
      width:50%;
      height:40px;
      border-radius: 6px;
      font-weight: 700;
      font-size: 18px;
      cursor:pointer;
      color:#fff;
    }
    .modal-body .tab span.active{
      background: #304352;
    }
    .mt_25{
      margin-top:25px;
    }
    .mt-10{
      margin-top:10px;
    }
    .btn-close-custom{
      position:absolute;
      right:28px;
      width:12px;
      height:12px;
      cursor:pointer;
    }
`;
/** base tool start  */
const wnearbase64 =
  "data:image/png;base64,";
const closeButtonBase64 =
  "data:image/png;base64,";
let MAX_RATIO = 10_000;
let BURROW_CONTRACT = "contract.main.burrow.near";
let accountId = context.accountId;
let B = Big();
B.DP = 60; // set precision to 60 decimals

const toAPY = (v) => Math.round(v * 100) / 100;
const clone = (o) => JSON.parse(JSON.stringify(o));
const shrinkToken = (value, decimals) => {
  return B(value).div(B(10).pow(decimals));
};

const expandToken = (value, decimals) => {
  return B(value).mul(B(10).pow(decimals));
};

const formatToken = (v) => Math.floor(v * 10_000) / 10_000;

const power = (x, y) => {
  if (y === 0) {
    return 1;
  } else if (y % 2 === 0) {
    return power(x, parseInt(y / 2)) * power(x, parseInt(y / 2));
  } else {
    return x * power(x, parseInt(y / 2)) * power(x, parseInt(y / 2));
  }
};
const nFormat = (num, digits) => {
  const lookup = [
    { value: 1, symbol: "" },
    { value: 1e3, symbol: "k" },
    { value: 1e6, symbol: "M" },
  ];
  const rx = /\.0+$|(\.[0-9]*[1-9])0+$/;
  var item = lookup
    .slice()
    .reverse()
    .find((item) => num >= item.value);
  return item
    ? (num / item.value).toFixed(digits).replace(rx, "$1") + item.symbol
    : "0";
};
const {
  rewards,
  account,
  balances,
  selectedTokenId,
  amount,
  hasError,
  assets,
} = state;
const hasData = assets.length > 0 && rewards.length > 0 && account;
/** base tool end */
if (!accountId) {
  return <Widget src="juaner.near/widget/ref_account-signin" />;
}
const config = Near.view(BURROW_CONTRACT, "get_config");
const onLoad = (data) => {
  State.update(data);
};
const rewardsMap = rewards
  ? rewards.reduce((acc, cur) => {
      return {
        ...acc,
        [cur.token_id]: cur,
      };
    }, {})
  : {};
const assetsMap = assets
  ? assets.reduce((acc, cur) => {
      return {
        ...acc,
        [cur.token_id]: cur,
      };
    }, {})
  : {};
// get market can burrow assets
const can_burrow_assets = assets && assets.filter((a) => a.config.can_borrow);
const market_burrow_assets =
  can_burrow_assets &&
  can_burrow_assets.map((asset) => {
    const { token_id, metadata } = asset;
    const r = rewards.find((a) => a.token_id === asset.token_id);
    const borrowApy = r.apyBaseBorrow;
    const liquidity = nFormat(asset.availableLiquidity, 2);
    const hasRewards = rewardsMap[token_id] && assetsMap[token_id];
    const rewardMap = hasRewards && rewardsMap[token_id];
    const rewardTokens = rewardMap && rewardMap.rewardTokensBorrow;
    const rewardTokensImg =
      rewardTokens &&
      rewardTokens.map((token_id, index) => {
        const metadata = assetsMap[token_id].metadata;
        return (
          <img
            class={`rewardIcon ${index > 0 ? "ml_4_ne" : ""}`}
            src={metadata.icon}
          ></img>
        );
      });
    return (
      <tr
        data-bs-toggle="modal"
        data-bs-target="#exampleModal"
        onClick={() => {
          handleSelect(token_id);
        }}
      >
        <td>
          <img src={metadata.icon || wnearbase64} class="tokenIcon"></img>
          {metadata.symbol}
        </td>
        <td>{toAPY(borrowApy)}%</td>
        <td>{rewardTokensImg}</td>
        <td class="text-end">{liquidity}</td>
      </tr>
    );
  });
console.log("000000000000-market_burrow_assets", can_burrow_assets);
const handleSelect = (token_id) => {
  State.update({
    selectedTokenId: token_id,
    amount: "",
    hasError: false,
  });
};
const selectedToken = (selectedTokenId && assetsMap[selectedTokenId]) || {};
const selectedTokenMeta = selectedToken.metadata || {};
return (
  <Container>
    {/* load data */}
    {!hasData && (
      <Widget src="juaner.near/widget/ref_burrow-data" props={{ onLoad }} />
    )}
    {/* market */}
    <div class="fw-bold text-white mt-3">
      <span class="text_red_color">Borrow</span> Market
    </div>
    <table class="table click">
      <thead>
        <tr>
          <th scope="col" width="25%">
            Assets
          </th>
          <th scope="col" class="text-start" width="25%">
            APY
          </th>
          <th scope="col" class="text-start" width="25%">
            Rewards
          </th>
          <th scope="col" class="text-end">
            Total Liquidity
          </th>
        </tr>
      </thead>
      <tbody>{market_burrow_assets}</tbody>
    </table>
    {/* Modal*/}
    <div
      class="modal fade"
      id="exampleModal"
      tabindex="-1"
      aria-labelledby="exampleModalLabel"
      aria-hidden="true"
    >
      <div class="modal-dialog">
        <div class="modal-content">
          <div class="modal-header">
            <div class="flex_center">
              <img
                class="tokenIcon"
                src={selectedTokenMeta.icon || wnearbase64}
              />
              {selectedTokenMeta.symbol}
            </div>
            <img
              class="btn-close-custom"
              data-bs-dismiss="modal"
              aria-label="Close"
              src={closeButtonBase64}
            />
          </div>
          <div class="modal-body">
            <div class="tab">
              <span class="active">Borrow</span>
              <span>Repay</span>
            </div>
            <Widget
              src="juaner.near/widget/ref-market-burrow-burrow"
              props={{ selectedTokenId }}
            />
          </div>
        </div>
      </div>
    </div>
  </Container>
);
