const Backdrop = styled.div`
  height: 100vh;
  width: 100vw;
  background-color: rgba(0, 0, 0, 0.6);
  position: fixed;
  left: 0;
  top: 0;
  z-index: 1001;
`;
const Modal = styled.div`
  background-color:#1A2E33;
  border-radius:12px;
  position:fixed;
  z-index:1002;
  width:30rem;
  max-width: 95vw;
  max-height: 80vh;
  padding:10px 0 20px 0;
  animation:anishow 0.3s forwards ease-out;
  left:50%;
  top:50%;
  @keyframes anishow {
    from {
      opacity: 0;
      transform:translate(-50%,-70%);
    }
    to {
      opacity: 1;
      transform:translate(-50%,-50%);
    }
  }
    .modal-header{
      display:flex;
      align-items:center;
      justify-content:center;
      color:#fff;
      font-weight: 700;
      font-size: 18px;
      padding:12px;
      margin-bottom:16px;
      border-bottom:2px solid rgba(48, 67, 82, 0.5);
    } 
    .modal-header .btn-close{
      position:absolute;
      right:28px;
      margin:0;
    }
    .modal-body {
        padding:0 10px;
    }
    .modal-body .tab{
      display:flex;
      align-items:center;
      justify-content:space-between;
      margin-bottom:30px;
    }
    .modal-body .tab span{
      display:flex;
      align-items:center;
      justify-content:center;
      width:50%;
      height:40px;
      border-radius: 6px;
      font-weight: 700;
      font-size: 18px;
      cursor:pointer;
      color:#fff;
    }
    .modal-body .tab span.active{
      background: #304352;
    }
   .btn-close-custom{
      position:absolute;
      right:28px;
      width:12px;
      height:12px;
      cursor:pointer;
    }
`;
/** base tool start  */
const wnearbase64 =
  "data:image/png;base64,";
const closeButtonBase64 =
  "data:image/png;base64,";
function changeTab(tabName) {
  State.update({
    tabName,
  });
}
const {
  suppliedAssets,
  selectedTokenId,
  selectedTokenMeta,
  type,
  showModal,
  closeModal,
} = props;
const show = showModal && type == "supply";
return (
  <>
    <div class="title">You Supplied</div>
    <table class="table click">
      <thead>
        <tr>
          <th scope="col" width="25%">
            Assets
          </th>
          <th scope="col" class="text-start" width="25%">
            Supply APY
          </th>
          <th scope="col" class="text-start">
            Balance
          </th>
        </tr>
      </thead>
      <tbody>{suppliedAssets}</tbody>
    </table>
    {/** modal */}
    <Modal style={{ display: show ? "block" : "none" }}>
      <div class="modal-header">
        <div class="flex_center">
          <img class="tokenIcon" src={selectedTokenMeta.icon || wnearbase64} />
          {selectedTokenMeta.symbol}
        </div>
        <img
          class="btn-close-custom"
          src={closeButtonBase64}
          onClick={closeModal}
        />
      </div>
      <div class="modal-body">
        <Widget
          src="juaner.near/widget/ref-market-supply-supply"
          props={{ selectedTokenId, showModal: show }}
        />
      </div>
    </Modal>
    <Backdrop
      style={{ display: show ? "block" : "none" }}
      onClick={closeModal}
    ></Backdrop>
  </>
);
