const Theme = styled.div`
    padding: 0;
    margin:0;
    width: 100%;
    min-width: 1000px;
    height: 100%;
    min-height: 400px;
    display: grid;
    place-items: center;
    padding-top: 100px;
    padding-bottom: 100px;
    margin: 0 auto;
    background: url(https://bafybeifnxzoabfcn4txprj7pkexb4adkcxnp34qfnsk3wkztay2sweuh44.ipfs.w3s.link/polygon-bg-optimized.png) no-repeat center center; 
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
    background-color: #0D0E20;

    .grid {
      display: flex;
      > div {
        padding: 16px;
      }
    }

    .apps {
      >h1 {
        color: white;
      }
      >h2 {
        color: rgba(255, 255, 255, 0.8);
        font-size: 1.4rem;
        margin-bottom: 32px;
      }
      >div {
        display: flex;
      > div {
        width: 150px;
        height: 150px;
        margin: 8px;
        color: white;
        text-align: center;
        > img {
          width: 100px;
        }
        >h1 {
          margin: 16px 0;
          font-size: 1rem;
        }
        > button {
          background: rgba(255, 255, 255, 0.5);
          border: none;
          border-radius: 24px;
          font-size: 0.8rem;
        }
}
      }
    }

    .center {
      display: grid;
      align-items: center;
      justify-items: center;
    }

    .logo {
        margin: 32px 0px;
    }

    .radio {
        display: inline-flex;
        justify-content: center;
        border-radius: 999px;
        background-color: #353535;

        .item {
          border: none;
          font-family: 'Inter';
          font-style: normal;
          font-weight: 600;
          font-size: 12px;
          line-height: 15px;
          padding: 6px 8px;
          border-radius: 999px;
          background-color: #353535;
          color: white;
        }

        .item[data-state='checked'] {
          background-color: #8247E5;
          border-radius: 999px;
        }
    }

    .component {
      margin-top: 1.5rem;
    }
`;

State.init({ radio: "bridge" });

const handleRadioChange = (radio) => {
  State.update({ radio });
};

const { radio } = state;

return (
  <Theme>
    <div className="grid">
