          </RadioGroup.Item>
        </RadioGroup.Root>
        <div className="component">
          {radio === "bridge" ? (
            <Widget src="ciocan.near/widget/zkevm-bridge" />
          ) : (
            <Widget src="zavodil.near/widget/swap-styled" />
          )}
        </div>
      </div>
    </div>
  </Theme>
