State.init({
  selectedQuiz: quizzes[Math.floor(Math.random() * quizzes.length)],
  select: "",
});

const onClickSubmit = () => {
  if (state.selectedQuiz.correctId === state.select) {
    State.update({
      ...state,
      selectedQuiz: {
        ...state.selectedQuiz,
        options: state.selectedQuiz.options.map((option) =>
          option.id === state.select
            ? { ...option, result: "right" }
            : { ...option, result: undefined }
        ),
      },
    });
  } else {
    State.update({
      ...state,
      selectedQuiz: {
        ...state.selectedQuiz,
        options: state.selectedQuiz.options.map((option) =>
          option.id === state.select
            ? { ...option, result: "wrong" }
            : { ...option, result: undefined }
        ),
      },
    });
  }
};

