const NEAR_QUIZ_STORE_KEY = "NEAR_QUIZ";
function getSelectedQuiz() {
  const cachedQuiz = Storage.get(NEAR_QUIZ_STORE_KEY);
  console.log({ cachedQuiz });
  const selectedQuiz =
    cachedQuiz || quizzes[Math.floor(Math.random() * quizzes.length)];
  if (!cachedQuiz) Storage.set(NEAR_QUIZ_STORE_KEY, selectedQuiz);
  return selectedQuiz;
}

