});

const onClickSubmit = () => {
  if (state.selectedQuiz.correctId === state.select) {
    State.update({
      ...state,
      selectedQuiz: {
        ...state.selectedQuiz,
        options: state.selectedQuiz.options.map((option) =>
          option.id === state.select
            ? { ...option, result: "right" }
            : { ...option, result: undefined }
        ),
      },
      showPost: true,
    });
    Storage.set("", true);
  } else {
    State.update({
      ...state,
      selectedQuiz: {
        ...state.selectedQuiz,
        options: state.selectedQuiz.options.map((option) =>
          option.id === state.select
            ? { ...option, result: "wrong" }
            : { ...option, result: undefined }
        ),
      },
    });
  }
};

