import { Form, FormGroup, Input, Label } from "@radix-ui/react-forms";
import { styled } from "@stitches/react";
import React, { useState } from "react";

// Define styles for the form
const StyledForm = styled(Form, {
  display: "flex",
  flexDirection: "column",
  gap: "16px",
  maxWidth: "400px",
});

// Define styles for the inputs
const StyledInput = styled(Input, {
  border: "1px solid gray",
  padding: "8px",
  borderRadius: "4px",
  fontSize: "16px",
});

// Define the form component
function MyForm() {
  // Declare state for the form inputs
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [nearAddress, setNearAddress] = useState("");

  // Handle form submission
  function handleSubmit(event) {
    event.preventDefault();
    console.log("Name:", name);
    console.log("Email:", email);
    console.log("NEAR address:", nearAddress);
    // Add code here to submit the form data to a server or API
  }

  return (
    <StyledForm onSubmit={handleSubmit}>
      <FormGroup>
        <Label htmlFor="name">Name</Label>
        <StyledInput
          id="name"
          type="text"
          value={name}
          onChange={(event) => setName(event.target.value)}
          placeholder="Enter your name"
          required
        />
      </FormGroup>
      <FormGroup>
        <Label htmlFor="email">Email</Label>
        <StyledInput
          id="email"
          type="email"
          value={email}
          onChange={(event) => setEmail(event.target.value)}
          placeholder="Enter your email"
          required
        />
      </FormGroup>
      <FormGroup>
        <Label htmlFor="nearAddress">NEAR Address</Label>
        <StyledInput
          id="nearAddress"
          type="text"
          value={nearAddress}
          onChange={(event) => setNearAddress(event.target.value)}
          placeholder="Enter your NEAR address"
          required
        />
      </FormGroup>
      <button type="submit">Submit</button>
    </StyledForm>
  );
}

export default MyForm;
