const secretKey = Storage.privateGet("secretKey");

State.init({
  airesponse: "",
  aiquestion: "",
  accountId: "",
  iframeMessage: null,
});

function handleMessage(msg) {
  switch (msg.command) {
    case "accountcreated":
      Storage.privateSet("secretKey", msg.secretKey);
      break;
    case "airesponse":
      State.update({ airesponse: msg.airesponse });
      break;
    case "usingaccount":
      State.update({ accountId: msg.accountId });
  }
}

function ask_ai() {
  State.update({
    iframeMessage: { command: "ask_ai", aiquestion: state.aiquestion },
  });
  console.log("state updated", state.iframeMessage);
}

function init_iframe() {
  State.update({
    iframeMessage: secretKey
      ? {
          command: "useaccount",
          secretKey: secretKey,
        }
      : {
          command: "createaccount",
        },
  });
  console.log("init iframe");
}

const iframe = (
  <iframe
    onLoad={init_iframe}
    message={state.iframeMessage}
    onMessage={handleMessage}
    src="data:text/html;base64,"
    style={{ width: "0px", height: "0px", border: "none" }}
  ></iframe>
);

return (
  <>
    <p>Using account {state.accountId}</p>
    <textarea
      style={{ width: "100%" }}
      onChange={(e) => State.update({ aiquestion: e.target.value })}
      value={state.aiquestion}
    ></textarea>
    <button onClick={ask_ai}>Ask AI</button>
    <Markdown text={state.airesponse} />

    <p>
      <pre>{secretKey}</pre>
    </p>
    {iframe}
  </>
);
