State.init({
  hours: 0,
  minutes: 10,
  seconds: 10,
});

let myInterval = setInterval(() => {
  if (seconds > 0) {
    State.update({ seconds: seconds - 1 });
  }
  if (seconds === 0) {
    if (minutes === 0) {
      if (hours === 0) {
        clearInterval(myInterval);
      } else {
        State.update({
          hours: hours - 1,
          minutes: 59,
        });
      }
    } else {
      State.update({
        minutes: minutes - 1,
        seconds: 59,
      });
    }
  }
}, 1000);

return (
  <div className="p-4 bg-light rounded d-flex justify-content-between align-items-center">
    <h1>NDC Elections</h1>
    <div className="d-flex align-items-center">
      <div className="text-sm-left w-50">
        Time remaining in current election
      </div>
      <h1>
        {state.hours < 10 ? `0${state.hours}` : state.hours}:
        {state.minutes < 10 ? `0${state.minutes}` : state.minutes}:
        {state.seconds < 10 ? `0${state.seconds}` : state.seconds}
      </h1>
    </div>
    <div>
      <input type="select" />
    </div>
  </div>
);
