let user_account = context.accountId;

const contract = "chatme.near";
const greeting = Near.view(contract, "get_greeting", {});

State.init({ greeting: "have a great day!!!" });
const onChange = ({ target }) => {
  State.update({ greeting: target.value });
};

const getPrivateChatId = (user1, user2) => {
  if (user1 > user2) {
    return user1.concat("|").concat(user2);
  }
  return user2.concat("|").concat(user1);
};

const sourceURL =
  "https://api.thegraph.com/subgraphs/name/vlodkomr/chatme-main";

fetch(sourceURL, {
  method: "GET",
  headers: {
    "Content-Type": "application/json",
  },
  body: JSON.stringify({
    query: `
        privateMessages(
          last: 100, 
          skip: 0,
          orderBy: created_at, 
          orderDirection: desc,
          where: {
            chat_id: "${getPrivateChatId("vlodkow.near", "chatme.near")}",
        }) {
            id
            inner_id
            text
            image
            from_address
            to_address
            deposit
            created_at
            encrypt_key
            reply_message {id, from_address, text, image, encrypt_key}
        }
      `,
  }),
})
  .then((res) => res.json())
  .then((result) => console.log(result));

return (
  <>
    <div class="container border border-info p-2 text-center">
      <h1>Hello World! {user_account}</h1>
      <hr />
      {user_account && (
        <>
          <p>{state.greeting}</p>
          <input onChange={onChange} />
        </>
      )}
    </div>
  </>
);
