const virtualPetContract = "0xFD70F326b5c31AbFb073C36fCf9b692f0F9D21AC";

const virtualPetAbi = fetch(
  "https://indigo-fluttering-emu-841.mypinata.cloud/ipfs/QmQ21DUmQw6FdYS8T7dSeS7syDAKoFP2P3j3CE1BdPizeE"
);

if (!virtualPetAbi.ok) {
  return "Loading";
}

const iface = new ethers.utils.Interface(virtualPetAbi.body);

State.init({
  myVirtualPets: [],
});

const ItemBackground = styled.div`
        width: 100%;
        height: 100vh;
        display: flex;
        justify-content: center;
        margin-bottom: -50px;
        background-image: url('https://pin.ski/444ghZP');
        background-repeat: no-repeat;
        background-size: cover;
        `;

const ItemContainer = styled.div`
        margin-top: 30px;
        box-sizing: border-box;
        min-width: 700px;
        max-width: 900px;
        width: 100%;
        padding: 0px 32px;
        position: relative;
        `;

const ItemTitle = styled.h3`
        font-weight: 900;
        text-align: center;
        color: white;
        display: flex;
        justify-content: center;
        align-items: center;
        margin-bottom: 1rem;
        `;

const ItemImage = styled.img`
            width: 40px;
            margin-right: 15px;
        `;

const ItemHeader = styled.div`
        background: rgb(242,167,115);
        font-weight: 400;
        font-size: 12px;
        line-height: 1.6em;
        border-radius: 20px;
        margin: 0px;
        padding: 20px;
        box-shadow: none;
        color: rgb(255, 255, 255);
        border: rgb(139,68,37);
        border-top-style: outset;
        `;

const ItemBody = styled.div`
        font-weight: 400;
        font-size: 1em;
        line-height: 1.6em;
        border-radius: 0px 0px 20px 20px;
        margin: -20px 0px 0px;
        padding: 15px;
        box-shadow: none;
        background: white;
        color: rgb(242,167,115);
        min-height: 500px;
        max-height: auto;
        border: solid 5px;
        `;

const ItemMintButton = styled.button`
        background: gray;
        color: rgb(255, 255, 255);
        font-weight: 700;
        padding: 15px 20px;
        border-radius: 1rem;
        border: none;
        &:hover {
            background: black;
        }
        `;

const ItemPetsSection = styled.div`
        gap: 3rem  7rem !important;
        display: grid;
        gap: 10px;
        grid-template-columns: repeat(auto-fit, 160px);
        justify-content: center;
        min-height: 500px;
        max-height: 550px;
        overflow:scroll;
        margin-inline: -10px;
        `;

const ItemPet = styled.div`
    gap: 0.25rem !important;
    padding: 1rem !important;
    flex-direction: column !important;
    display: flex !important;
    border: solid 3px;
    border-radius: 20px;
    color: rgb(0 0 0 / 30%);
    background: rgb(0 0 0 / 7%);
    min-width: 170px;
    align-items: center;
    cursor: pointer;
`;

const ItemPetName = styled.label`
    color: black;
    font-weight: 700;
`;

const ItemPetImg = styled.img`
    width: 150px;
`;

const ItemNoPets = styled.div`
    width: 100%;
    color: black;
    margin-top: 50px;
    text-align: center;
}
`;

if (state.sender === undefined) {
  const accounts = Ethers.send("eth_requestAccounts", []);
  if (accounts.length) {
    State.update({ sender: accounts[0] });
  }
}

if (state.sender) {
  const contract = new ethers.Contract(
    virtualPetContract,
    virtualPetAbi.body,
    Ethers.provider().getSigner()
  );

  // Consultar virtualpets del usuario
  contract.getTokensInfoByOwner().then((res) => {
    const petsInfo = res.map(_castData);
    console.log(petsInfo);
    State.update({ myVirtualPets: petsInfo });
  });
}

const _castData = (data) => {
  return {
    id: data[0],
    image: data[1],
    name: data[2],
    happiness: data[3],
    hunger: data[4],
    currentActivity: data[5],
  };
};

const getSender = () => {
  return !state.sender
    ? ""
    : state.sender.substring(0, 6) +
        "..." +
        state.sender.substring(state.sender.length - 4, state.sender.length);
};

// FETCH CSS

const cssFont = fetch(
  "https://fonts.googleapis.com/css2?family=Manrope:wght@200;300;400;500;600;700;800"
).body;
const css = fetch(
  "https://pluminite.mypinata.cloud/ipfs/Qmboz8aoSvVXLeP5pZbRtNKtDD3kX5D9DEnfMn2ZGSJWtP"
).body;

if (!cssFont || !css) return "";

if (!state.theme) {
  State.update({
    theme: styled.div`
    font-family: Manrope, -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue, sans-serif;
    ${cssFont}
    ${css}
`,
  });
}
const Theme = state.theme;

return (
  <Theme>
    <ItemBackground>
      <ItemContainer>
        <ItemHeader>
          <ItemTitle class="row">
            <div class="col-4" style={{ "text-align": "left" }}>
              <ItemImage src="https://pin.ski/3Jkha80"></ItemImage>
            </div>
            <div class="col-4">My Virtual Pets</div>
            <div class="col-4" style={{ "text-align": "right" }}>
              {state.sender ? (
                <button
                  style={{
                    "box-shadow":
                      "0 0px 13px 0 rgba(0,0,0,0.2), 0 0px 13px 0 rgba(0,0,0,0.19)",
                  }}
                  onClick={async () => {
                    mint();
                  }}
                >
                  Buy Pet
                </button>
              ) : null}
            </div>
          </ItemTitle>
        </ItemHeader>
        <ItemBody>
          <div class="container text-center">
            {state.sender ? (
              <div>
                <ItemPetsSection>
                  {state.myVirtualPets.length > 0 ? (
                    state.myVirtualPets.map((pet, i) => {
                      return (
                        <div>
                          <a
                            //href={`#/yairnava.near/widget/VirtualPetsInteract-BurritoBattle?tokenId=${pet.id}`}
                            href="#/yairnava.near/widget/BookStore"
                            className="btn mt-4 btn-outline-light float-end position-relative"
                            style={{ zIndex: 1 }}
                          >
                            <ItemPet>
                              <ItemPetImg src={pet.image}></ItemPetImg>
                            </ItemPet>
                          </a>

                          <ItemPetName>{pet.name}</ItemPetName>
                        </div>
                      );
                    })
                  ) : (
                    <ItemNoPets>
                      You don't have a virtual pet yet, go to the minteo section
                      to purchase one.
                    </ItemNoPets>
                  )}
                </ItemPetsSection>
              </div>
            ) : (
              <Web3Connect
                className="LidoStakeFormSubmitContainer"
                connectLabel="Connect with Web3 to View your Virtual Pets"
              />
            )}
          </div>
        </ItemBody>
      </ItemContainer>
    </ItemBackground>
  </Theme>
);
